// ProductDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const ProductDetails = () => {
  const { productId } = useParams(); // Get productId from the URL
  const [product, setProduct] = useState(null);

  // Fetch product details based on productId
  useEffect(() => {
    // Assuming you have a backend API or local products array
    fetch(`/api/products/${productId}`) // Example API endpoint
      .then((response) => response.json())
      .then((data) => setProduct(data))
      .catch((error) => console.error('Error fetching product:', error));
  }, [productId]); // Re-fetch when the productId changes

  // If the product is still loading or not found, show a loading or error message
  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="py-12 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="lg:grid lg:grid-cols-2 lg:gap-8">
          {/* Product Image */}
          <div className="lg:max-w-lg lg:w-full lg:h-full">
            <img
              src={product.imageUrl}
              alt={product.name}
              className="w-full h-full object-center object-cover"
            />
          </div>

          {/* Product Information */}
          <div className="mt-4 lg:mt-0 lg:col-span-1">
            <h1 className="text-3xl font-extrabold text-gray-900">{product.name}</h1>
            <p className="text-xl text-gray-700 mt-4">{product.description}</p>

            {/* Price */}
            <p className="text-2xl font-semibold text-gray-900 mt-6">{product.price} â‚¹</p>

            {/* Add to Cart Button */}
            <button className="mt-6 px-4 py-2 bg-indigo-600 text-white font-semibold rounded-md">
              Add to Cart
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
