import React, { useState, useEffect } from "react";
import { boysuniformsData } from "./boysuniformsData";
import { girlsuniformData } from "./girlsuniformData";
import ProductCard from "./ProductCard";

const ProductPage = () => {
  // States for filtering and pagination
  const [gender, setGender] = useState("All");
  const [institutionType, setInstitutionType] = useState("All");
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(100);
  const [currentPage, setCurrentPage] = useState(1);
  const [productsPerPage] = useState(8);

  const allProducts = [
    ...boysuniformsData.map((item) => ({ ...item, category: "Boys" })),
    ...girlsuniformData.map((item) => ({ ...item, category: "Girls" })),
  ];

  // Filtering logic
  const filteredProducts = allProducts.filter((product) => {
    const withinPriceRange =
      product.price >= minPrice && product.price <= maxPrice;
    const matchesGender = gender === "All" || product.gender === gender;
    const matchesInstitution =
      institutionType === "All" || product.institution === institutionType;
    return withinPriceRange && matchesGender && matchesInstitution;
  });

  // Pagination logic
  const indexOfLastProduct = currentPage * productsPerPage;
  const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
  const currentProducts = filteredProducts.slice(indexOfFirstProduct, indexOfLastProduct);

  // Handling page change
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  useEffect(() => {
    setCurrentPage(1); // Reset to page 1 on filter change
  }, [gender, institutionType, minPrice, maxPrice]);

  return (
    <div className="container mx-auto p-4">
      {/* Filter Section */}
      <div className="flex flex-wrap justify-between gap-4 mb-8">
        <select
          className="border p-2 rounded-md"
          value={gender}
          onChange={(e) => setGender(e.target.value)}
        >
          <option value="All">All Genders</option>
          <option value="Boys">Boys</option>
          <option value="Girls">Girls</option>
        </select>
        <select
          className="border p-2 rounded-md"
          value={institutionType}
          onChange={(e) => setInstitutionType(e.target.value)}
        >
          <option value="All">All Institutions</option>
          <option value="School">School</option>
          <option value="College">College</option>
        </select>
        <div className="flex gap-2">
          <input
            type="number"
            placeholder="Min Price"
            value={minPrice}
            onChange={(e) => setMinPrice(Number(e.target.value))}
            className="border p-2 rounded-md w-24"
          />
          <input
            type="number"
            placeholder="Max Price"
            value={maxPrice}
            onChange={(e) => setMaxPrice(Number(e.target.value))}
            className="border p-2 rounded-md w-24"
          />
        </div>
      </div>

      {/* Product Cards Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {currentProducts.map((product) => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>

      {/* Pagination */}
      <div className="flex justify-center mt-8">
        <nav className="pagination flex space-x-2">
          {Array.from({ length: Math.ceil(filteredProducts.length / productsPerPage) }).map((_, index) => (
            <button
              key={index + 1}
              onClick={() => paginate(index + 1)}
              className={`px-4 py-2 border rounded-md ${
                currentPage === index + 1 ? "bg-blue-500 text-white" : "bg-white text-blue-500"
              }`}
            >
              {index + 1}
            </button>
          ))}
        </nav>
      </div>
    </div>
  );
};

export default ProductPage;
