import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const LoginPage = () => {
  const [userType, setUserType] = useState('student'); // default to 'student'
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [studentId, setStudentId] = useState('');
  const [childStudentId, setChildStudentId] = useState('');
  const [institutionName, setInstitutionName] = useState('');
  const [contactNo, setContactNo] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Clear form fields whenever user type changes
    setEmail('');
    setPassword('');
    setStudentId('');
    setChildStudentId('');
    setInstitutionName('');
    setContactNo('');
    setError('');
  }, [userType]);

  const handleUserTypeChange = (type) => {
    setUserType(type);
  };

  const handleToggleLoginRegister = () => {
    setIsLogin(!isLogin);
    navigate(isLogin ? '/register' : '/login');
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear any previous errors
  
    // Trim whitespace before validation
    const trimmedEmail = email.trim();
    const trimmedPassword = password.trim();
  
    console.log('Email:', trimmedEmail);
    console.log('Password:', trimmedPassword);
  
    if (!trimmedEmail || !trimmedPassword) {
      setError('Email and password are required.');
      return;
    }
  
    try {
      let apiUrl = 'http://localhost:3002/api/auth/login/submit'; // Adjust according to your backend API URL
  
      const response = await axios.post(apiUrl, {
        email: trimmedEmail,
        password: trimmedPassword,
        userType,
        studentId,
        childStudentId,
        institutionName,
        contactNo
      });
  
      console.log(response.data); // Log the response for debugging
  
      if (response.data.success) {
        // Handle successful login (redirect to respective dashboard)
        if (userType === 'admin') {
          navigate('/admin'); // Admin dashboard
        } else {
          navigate('/dashboard'); // User dashboard
        }
      } else {
        setError(response.data.message || 'Invalid credentials');
      }
    } catch (err) {
      setError('Error: Unable to login. Please try again.');
      console.error(err);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4 sm:p-8 lg:p-12">
      <div className="w-full max-w-md bg-white shadow-lg rounded-lg p-6 sm:p-8 lg:p-10">
        <h2 className="text-2xl sm:text-3xl font-semibold text-gray-700 text-center mb-4">
          {isLogin ? 'Login' : 'Register'} as {userType.charAt(0).toUpperCase() + userType.slice(1)}
        </h2>

        {/* User Type Selector */}
        <div className="flex justify-center mt-4 space-x-2 sm:space-x-4">
          {['student', 'parent', 'institution', 'admin'].map((type) => (
            <button
              key={type}
              className={`py-2 px-4 rounded-lg font-semibold ${userType === type ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => handleUserTypeChange(type)}
            >
              {type.charAt(0).toUpperCase() + type.slice(1)}
            </button>
          ))}
        </div>

        {/* Form Fields */}
        <form className="mt-6 space-y-4" onSubmit={handleLoginSubmit}>
          {/* Render fields based on userType */}
          {userType === 'student' && (
            <div>
              <label className="block text-sm text-gray-600">Student ID</label>
              <input
                type="text"
                value={studentId}
                onChange={(e) => setStudentId(e.target.value)}
                placeholder="Enter your student ID"
                className="w-full px-4 py-2 mt-1 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
              />
            </div>
          )}

          {userType === 'parent' && (
            <>
              <div>
                <label className="block text-sm text-gray-600">Parent Email</label>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Enter your email"
                  className="w-full px-4 py-2 mt-1 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
                />
              </div>
              <div>
                <label className="block text-sm text-gray-600">Childâ€™s Student ID</label>
                <input
                  type="text"
                  value={childStudentId}
                  onChange={(e) => setChildStudentId(e.target.value)}
                  placeholder="Enter your child's student ID"
                  className="w-full px-4 py-2 mt-1 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
                />
              </div>
            </>
          )}

          {userType === 'institution' && (
            <>
              <div>
                <label className="block text-sm text-gray-600">Institution Name</label>
                <input
                  type="text"
                  value={institutionName}
                  onChange={(e) => setInstitutionName(e.target.value)}
                  placeholder="Enter your institution name"
                  className="w-full px-4 py-2 mt-1 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
                />
              </div>
              <div>
                <label className="block text-sm text-gray-600">Contact No.</label>
                <input
                  type="number"
                  value={contactNo}
                  onChange={(e) => setContactNo(e.target.value)}
                  placeholder="Enter contact number"
                  className="w-full px-4 py-2 mt-1 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
                />
              </div>
              <div>
                <label className="block text-sm text-gray-600">Email</label>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Enter your institution email"
                  className="w-full px-4 py-2 mt-1 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
                />
              </div>
            </>
          )}

          {userType === 'admin' && (
            <>
              <div>
                <label className="block text-sm text-gray-600">Email</label>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Enter admin email"
                  className="w-full px-4 py-2 mt-1 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
                />
              </div>
              <div>
                <label className="block text-sm text-gray-600">Password</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter admin password"
                  className="w-full px-4 py-2 mt-1 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
                />
              </div>
            </>
          )}

          {/* Common Password Field for Parents, Students, and Institutions */}
          {(userType === 'student' || userType === 'parent' || userType === 'institution') && (
            <div className="mt-4">
              <label className="block text-sm text-gray-600">Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                className="w-full px-4 py-2 mt-1 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
              />
            </div>
          )}

          <button
            type="submit"
            className="w-full mt-6 py-2 bg-indigo-600 text-white font-semibold rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-600"
          >
            {isLogin ? 'Login' : 'Register'}
          </button>

          {/* Error message */}
          {error && <p className="text-red-500 text-sm mt-2 text-center">{error}</p>}
        </form>

        {/* Toggle Login/Register */}
        <div className="text-center mt-4">
          <p className="text-sm text-gray-600">
            {isLogin ? "Don't have an account?" : 'Already have an account?'}{' '}
            <button
              onClick={handleToggleLoginRegister}
              className="text-indigo-600 font-medium hover:underline"
            >
              {isLogin ? 'Register' : 'Login'}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
