import React from 'react';
import { IconButton } from '@mui/material';
import RemoveCircleOutlineIcon from '@mui/icons-material/RemoveCircleOutline';
import ControlPointIcon from '@mui/icons-material/ControlPoint';
import Button from '@mui/material/Button';

const Cartitem = ({ item, setCartItems }) => {
  const { id, name, price, quantity } = item;

  // Update localStorage and state when quantity is incremented
  const handleIncrement = () => {
    const cart = JSON.parse(localStorage.getItem('cartItems')) || [];
    const updatedCart = cart.map((cartItem) =>
      cartItem.id === id
        ? { ...cartItem, quantity: cartItem.quantity + 1 }
        : cartItem
    );
    localStorage.setItem('cartItems', JSON.stringify(updatedCart));
    setCartItems(updatedCart); // Update the state to re-render the cart
  };

  // Update localStorage and state when quantity is decremented
  const handleDecrement = () => {
    const cart = JSON.parse(localStorage.getItem('cartItems')) || [];
    const updatedCart = cart.map((cartItem) =>
      cartItem.id === id && cartItem.quantity > 1
        ? { ...cartItem, quantity: cartItem.quantity - 1 }
        : cartItem
    );
    localStorage.setItem('cartItems', JSON.stringify(updatedCart));
    setCartItems(updatedCart); // Update the state to re-render the cart
  };

  // Remove item from the cart
  const handleRemove = () => {
    const cart = JSON.parse(localStorage.getItem('cartItems')) || [];
    const updatedCart = cart.filter((cartItem) => cartItem.id !== id);
    localStorage.setItem('cartItems', JSON.stringify(updatedCart));
    setCartItems(updatedCart); // Update the state to re-render the cart
  };

  return (
    <div className="p-5 shadow-lg border rounded-md mb-4">
      <div className="flex items-center">
        <div className="w-[5rem] h-[5rem] lg:w-[9rem] lg:h-[9rem]">
          <img
            src="https://3.imimg.com/data3/MN/ND/MY-12472581/girls-school-pant-500x500.jpg"
            alt="Product Image"
            className="object-cover object-top w-full h-full"
          />
        </div>

        <div className="ml-5 space-y-1">
          <h3 className="text-sm font-medium">{name}</h3>
          <p className="text-xs">Blue, High Quality</p>
          <p className="text-xs text-gray-500">Price: ${(price * quantity).toFixed(2)}</p>
        </div>
      </div>

      <div className="lg:flex items-center lg:space-x-10 pt-4">
        <div className="flex items-center space-x-2">
          <IconButton onClick={handleDecrement} sx={{ color: "RGB(145 85 253)" }}>
            <RemoveCircleOutlineIcon />
          </IconButton>

          <span className="py-1 px-7 border rounded-sm">{quantity}</span>

          <IconButton onClick={handleIncrement} sx={{ color: "RGB(145 85 253)" }}>
            <ControlPointIcon />
          </IconButton>
        </div>

        <div>
          <Button
            variant="text"
            sx={{ color: "RGB(145 85 253)" }}
            onClick={handleRemove}
          >
            Remove
          </Button>
        </div>
      </div>
    </div>
  );
};

export default Cartitem;
