import React from 'react';

const AddressCard = ({ address }) => {
  if (!address) {
    return <div>Loading address...</div>; // Show loading message if address is not available
  }
  
  useEffect(() => {
    const fetchAddress = async () => {
      try {
        const response = await axios.get("http://localhost:3002/api/Checkout/address");
        const address = response.data[0]; // Select the first address (or implement logic to select one)
        setAddress(address); // Set the address to state
      } catch (error) {
        console.error("Error fetching address:", error);
      }
    };
  
    fetchAddress();
  }, []);
  
  return (
    <div className="space-y-3">
      <p className="font-semibold">{address.firstName} {address.lastName}</p>
      <p className="font-semibold">{address.email}</p>
      <p>{address.address1}, {address.address2}, {address.city}, {address.state}, {address.zipCode}</p>
      <p>{address.country}</p>
      <div className="space-y-1">
        <p className="font-semibold">Phone Number</p>
        <p>{address.contactNumber}</p>
      </div>
    </div>
  );
};

export default AddressCard;
