import { Box, Button, Grid, TextField, Typography } from "@mui/material";
import React, { useState, useEffect } from "react";
import AddressCard from "../AddressCard/AddressCard";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const DeliveryAddressForm = () => {
  const [loading, setLoading] = useState(false);
  const [address, setAddress] = useState(null); // State to store fetched address

  useEffect(() => {
    // Fetch the saved address when the component mounts
    const fetchAddress = async () => {
      try {
        const response = await axios.get("http://localhost:3002/api/Checkout/address");
        setAddress(response.data); // Set the address state with fetched data
      } catch (error) {
        console.error("Error fetching address:", error);
      }
    };

    fetchAddress();
  }, []);

  const handlesubmit = async (e) => {
    e.preventDefault();
    const data = new FormData(e.currentTarget);

    const addressData = {
      firstName: data.get("firstName"),
      lastName: data.get("lastName"),
      email: data.get("email"),
      contactNumber: data.get("contactNumber"),
      address1: data.get("addressLine1"),
      address2: data.get("addressLine2"),
      city: data.get("city"),
      state: data.get("state"),
      zipCode: data.get("zipCode"),
      country: data.get("country"),
      institutionName: data.get("institutionName"),
      notes: data.get("notes") || "",
    };

    setLoading(true);
    try {
      const response = await axios.post("http://localhost:3002/api/Checkout/submit", addressData);
      toast.success("Address saved successfully!");
      setAddress(response.data); // Update the address in the state with the saved data
    } catch (error) {
      toast.error("Failed to save the address. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <Grid container spacing={4}>
        <Grid item xs={12} lg={5} className="border rounded-e-md shadow-md h-[30.5rem] overflow-y-scroll">
          <div className="p-5 py-7 border-b cursor-pointer">
            <AddressCard address={address} /> {/* Pass the address data to AddressCard */}
            <Button sx={{ mt: 2, bgcolor: "RGB(145 85 253)" }} size="large" variant="contained">
              Deliver Here
            </Button>
          </div>
        </Grid>

        <Grid item xs={12} lg={7}>
          <Box className="border rounded-s-md shadow-md p-5">
            <Typography variant="h6" gutterBottom>
              Delivery Address
            </Typography>
            <form onSubmit={handlesubmit}>
              <Grid container spacing={3}>
                {/* Form Fields */}
                <Grid item xs={12} sm={6}>
                  <TextField required id="firstName" name="firstName" label="First Name" fullWidth autoComplete="given-name" />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField required id="lastName" name="lastName" label="Last Name" fullWidth autoComplete="family-name" />
                </Grid>
                {/* Additional Fields */}
                {/* Submit Button */}
                <Grid item xs={12}>
                  <Button
                    sx={{ mt: 2, bgcolor: "RGB(145 85 253)" }}
                    size="large"
                    variant="contained"
                    type="submit"
                    fullWidth
                    disabled={loading}
                  >
                    {loading ? "Saving..." : "Save & Continue"}
                  </Button>
                </Grid>
              </Grid>
            </form>
          </Box>
        </Grid>
      </Grid>
      <ToastContainer />
    </div>
  );
};

export default DeliveryAddressForm;
