import { Box, Button, Grid, TextField, Typography } from "@mui/material";
import React, { useState } from "react";
import AddressCard from "../AddressCard/AddressCard";
import { ToastContainer, toast } from "react-toastify"; // Import from react-toastify
import "react-toastify/dist/ReactToastify.css"; // Import the CSS for Toastify
import axios from "axios";

const DeliveryAddressForm = () => {
  const [loading, setLoading] = useState(false); // To manage loading state

  const handlesubmit = async (e) => {
    e.preventDefault();

    const data = new FormData(e.currentTarget);

    const address = {
      firstName: data.get("firstName"),
      lastName: data.get("lastName"),
      email: data.get("email"),
      contactNumber: data.get("contactNumber"),
      address1: data.get("addressLine1"), 
      address2: data.get("addressLine2"), 
      city: data.get("city"),
      state: data.get("state"),
      zipCode: data.get("zipCode"),
      country: data.get("country"),
      institutionName: data.get("institutionName"),
      notes: data.get("notes") || "",
    };

    console.log("address", address);

    setLoading(true); // Show loading spinner or disable button

    try {
      const response = await axios.post("http://localhost:3002/api/Checkout/submit", address);
      console.log("Address saved successfully:", response.data);

      // Show success toast
      toast.success("Address saved successfully!", {
        position: "top-right",
        autoClose: 5000,  // Time for toast to disappear
        hideProgressBar: true, // Hide progress bar
        closeOnClick: true, // Close on click
        pauseOnHover: true, // Pause when hovered
        draggable: true, // Allow dragging
        progress: undefined,
      });

    } catch (error) {
      console.error("Error saving address:", error);

      // Show error toast
      toast.error("Failed to save the address. Please try again.", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: true,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });

    } finally {
      setLoading(false); // Hide loading state after request completes
    }
  };

  return (
    <div>
      <Grid container spacing={4}>
        <Grid item xs={12} lg={5} className="border rounded-e-md shadow-md h-[30.5rem] overflow-y-scroll">
          <div className="p-5 py-7 border-b cursor-pointer">
            <AddressCard />
            <Button sx={{ mt: 2, bgcolor: "RGB(145 85 253)" }} size="large" variant="contained">
              Deliver Here
            </Button>
          </div>
        </Grid>

        <Grid item xs={12} lg={7}>
          <Box className="border rounded-s-md shadow-md p-5">
            <Typography variant="h6" gutterBottom>
              Delivery Address
            </Typography>
            <form onSubmit={handlesubmit}>
              <Grid container spacing={3}>
                {/* First Name */}
                <Grid item xs={12} sm={6}>
                  <TextField required id="firstName" name="firstName" label="First Name" fullWidth autoComplete="given-name" />
                </Grid>

                {/* Last Name */}
                <Grid item xs={12} sm={6}>
                  <TextField required id="lastName" name="lastName" label="Last Name" fullWidth autoComplete="family-name" />
                </Grid>

                {/* Institution/School Name */}
                <Grid item xs={12}>
                  <TextField id="institutionName" name="institutionName" label="Institution/School Name" fullWidth autoComplete="organization" />
                </Grid>

                {/* Address Line 1 */}
                <Grid item xs={12}>
                  <TextField required id="addressLine1" name="addressLine1" label="Address Line 1" fullWidth autoComplete="shipping address-line1" />
                </Grid>

                {/* Address Line 2 */}
                <Grid item xs={12}>
                  <TextField id="addressLine2" name="addressLine2" label="Address Line 2" fullWidth autoComplete="shipping address-line2" />
                </Grid>

                {/* City */}
                <Grid item xs={12} sm={6}>
                  <TextField required id="city" name="city" label="City" fullWidth autoComplete="shipping address-level2" />
                </Grid>

                {/* State/Province */}
                <Grid item xs={12} sm={6}>
                  <TextField required id="state" name="state" label="State/Province" fullWidth />
                </Grid>

                {/* Postal Code */}
                <Grid item xs={12} sm={6}>
                  <TextField required id="zipCode" name="zipCode" label="Postal Code" fullWidth autoComplete="shipping postal-code" />
                </Grid>

                {/* Country */}
                <Grid item xs={12} sm={6}>
                  <TextField required id="country" name="country" label="Country" fullWidth autoComplete="shipping country" />
                </Grid>

                {/* Contact Number */}
                <Grid item xs={12} sm={6}>
                  <TextField required id="contactNumber" name="contactNumber" label="Contact Number" fullWidth autoComplete="tel" />
                </Grid>

                {/* Email */}
                <Grid item xs={12} sm={6}>
                  <TextField required id="email" name="email" label="Email" fullWidth autoComplete="email" />
                </Grid>

                {/* Additional Notes */}
                <Grid item xs={12}>
                  <TextField id="notes" name="notes" label="Additional Notes (Optional)" multiline rows={3} fullWidth />
                </Grid>

                {/* Submit Button */}
                <Grid item xs={12}>
                  <Button
                    sx={{ mt: 2, bgcolor: "RGB(145 85 253)" }}
                    size="large"
                    variant="contained"
                    type="submit"
                    fullWidth
                    disabled={loading} // Disable the button while loading
                  >
                    {loading ? "Saving..." : "Save & Continue"}
                  </Button>
                </Grid>
              </Grid>
            </form>
          </Box>
        </Grid>
      </Grid>

      {/* Toast Container for the toast notifications */}
      <ToastContainer />
    </div>
  );
};

export default DeliveryAddressForm;
