import React, { useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify'; // Import Toastify components
import 'react-toastify/dist/ReactToastify.css'; // Import default styles for Toastify

const RegisterPage = () => {
  const [userType, setUserType] = useState('student'); // default to 'student'
  
  // Form data state
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    password: '',
    confirmPassword: '',
    studentId: '',
    gradeOrClass: '',
    childStudentId: '',
    institutionName: '',
    contactPerson: '',
    address: '',
  });

  const handleUserTypeChange = (type) => {
    setUserType(type);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Password validation
    if (formData.password !== formData.confirmPassword) {
      toast.error("Passwords don't match!", { autoClose: 3000 }); // Show error toast
      return;
    }

    try {
      // Send the form data to the backend
      const response = await axios.post('http://localhost:3002/api/auth/register/submit', formData);
      console.log('Registration successful:', response.data);
      
      toast.success('Registration successful! Welcome aboard.', {
        position: "top-right", // Position of the toast
        autoClose: 5000, // Auto close after 5 seconds
        hideProgressBar: false, // Optionally show a progress bar
        closeOnClick: true, // Close on click
        pauseOnHover: true, // Pause on hover
        draggable: true, // Allow dragging
        progress: undefined, // You can use a custom progress if you want
      });

      // Optionally, you can redirect the user after successful registration
      // window.location.href = '/login'; // Redirect to login page after successful registration
    } catch (error) {
      console.error('Error registering user:', error.response?.data || error);
      toast.error('Error registering user. Please try again later.', {
        autoClose: 3000, // Error notification auto close
      });
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4 sm:p-8">
      <div className="w-full max-w-lg sm:max-w-3xl bg-white shadow-lg rounded-lg p-6 sm:p-8">
        <h2 className="text-2xl sm:text-3xl font-semibold text-gray-700 text-center mb-4">
          Register as {userType.charAt(0).toUpperCase() + userType.slice(1)}
        </h2>

        {/* User Type Selector */}
        <div className="flex justify-center mt-4 space-x-2 sm:space-x-4">
          {['student', 'parent', 'institution'].map((type) => (
            <button
              key={type}
              className={`py-2 px-3 sm:px-4 rounded-lg font-semibold ${userType === type ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => handleUserTypeChange(type)}
            >
              {type.charAt(0).toUpperCase() + type.slice(1)}
            </button>
          ))}
        </div>

        {/* Form Fields */}
        <form onSubmit={handleSubmit} className="mt-6 grid grid-cols-1 gap-4 sm:grid-cols-2">
          {/* Render fields based on userType (student, parent, institution) */}
          {userType === 'student' && (
            <>
              <div>
                <label className="block text-sm text-gray-600">Full Name</label>
                <input
                  type="text"
                  name="fullName"
                  value={formData.fullName}
                  onChange={handleChange}
                  placeholder="Enter your full name"
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
                />
              </div>
              <div>
                <label className="block text-sm text-gray-600">Student ID</label>
                <input
                  type="text"
                  name="studentId"
                  value={formData.studentId}
                  onChange={handleChange}
                  placeholder="Enter your student ID"
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
                />
              </div>
              <div className="sm:col-span-2">
                <label className="block text-sm text-gray-600">Email Address</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="Enter your email"
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
                />
              </div>
              <div className="sm:col-span-2">
                <label className="block text-sm text-gray-600">Grade / Class</label>
                <input
                  type="text"
                  name="gradeOrClass"
                  value={formData.gradeOrClass}
                  onChange={handleChange}
                  placeholder="Enter your grade or class"
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
                />
              </div>
            </>
          )}

          {userType === 'parent' && (
            <>
              {/* Parent specific fields */}
            </>
          )}

          {userType === 'institution' && (
            <>
              {/* Institution specific fields */}
            </>
          )}

          {/* Common Password Fields */}
          <div>
            <label className="block text-sm text-gray-600">Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Enter your password"
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
            />
          </div>
          <div>
            <label className="block text-sm text-gray-600">Confirm Password</label>
            <input
              type="password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              placeholder="Confirm your password"
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-600"
            />
          </div>

          {/* Register Button */}
          <button
            type="submit"
            className="col-span-1 sm:col-span-2 w-full py-2 bg-indigo-600 text-white font-semibold rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-600"
          >
            Register
          </button>

          {/* Already have an account link */}
          <p className="text-center mt-4 text-sm text-gray-600 sm:col-span-2">
            Already have an account?{' '}
            <a href="/login" className="text-indigo-600 font-medium hover:underline">
              Login
            </a>
          </p>
        </form>
      </div>

      {/* Toast Container to display toasts */}
      <ToastContainer />
    </div>
  );
};

export default RegisterPage;
