'use client'

import { useEffect, useState } from 'react'
import { StarIcon } from '@heroicons/react/20/solid'
import { Radio, RadioGroup } from '@headlessui/react'
import { useNavigate, useParams } from 'react-router-dom'



function classNames(...classes) {
  return classes.filter(Boolean).join(' ')
}

export default function ProductDetails() {
  const {id}=useParams();
  const [product, setproduct] = useState();
  // const [selectedColor, setSelectedColor] = useState(product.colors[0])
  const [selectedSize, setSelectedSize] = useState(product.sizes[2])
  const navigate = useNavigate()

  const Handleaddtocart=()=>{
    navigate('/cart')

  }
  useEffect(() => {
    // Fetch product details based on id
    const fetchProduct = async () => {
      try {
        const response = await fetch(`/api/products/${id}`); // Adjust the endpoint as necessary
        const data = await response.json();
        setproduct(data);
      } catch (error) {
        console.error("Error fetching product details:", error);
      }
    };
    fetchProduct();
  },[id]);
  return (
    <div className="bg-white">
      <div className="pt-6">
        <nav aria-label="Breadcrumb">
          <ol role="list" className="mx-auto flex max-w-2xl items-center space-x-2 px-4 sm:px-6 lg:max-w-7xl lg:px-8">
            {product.breadcrumbs.map((breadcrumb) => (
              <li key={breadcrumb.id}>
                <div className="flex items-center">
                  <a href={breadcrumb.href} className="mr-2 text-sm font-medium text-gray-900">
                    {breadcrumb.name}
                  </a>
                  <svg
                    fill="currentColor"
                    width={16}
                    height={20}
                    viewBox="0 0 16 20"
                    aria-hidden="true"
                    className="h-5 w-4 text-gray-300"
                  >
                    <path d="M5.697 4.34L8.98 16.532h1.327L7.025 4.341H5.697z" />
                  </svg>
                </div>
              </li>
            ))}
            <li className="text-sm">
              <a href={product.href} aria-current="page" className="font-medium text-gray-500 hover:text-gray-600">
                {product.name}
              </a>
            </li>
          </ol>
        </nav>
            <section className='grid grid-cols-1 lg:grid-cols-2 gap-x-8 gap-y-10 px-4 pt-10'>
                 {/* Image gallery */}
                 <div className="flex flex-col items-center ">
                <div className="overflow-hidden rounded-lg max-w-[30rem] max-h-[35rem]">
                    <img
                    alt={product.images[0].alt}
                    src={product.images[0].src}
                    className="h-full w-full object-cover object-center"
                    />
                </div>
               
                </div>

                {/* Product info */}
                <div className="lg:col-span-1 max-auto max-w-2x1 px-4 pb-16 sm:px-6 lg:max-w-7xl lg:px-8  lg-pb-24 ">
                <div className="lg:col-span-2">
                    <h1 className="text-lg lg:text-xl font-semibold text-gray-900">{product.name}</h1>
                </div>
            
                {/* Options */}
                <div className="mt-4 lg:row-span-3 lg:mt-0">
                    <h2 className="sr-only">Product information</h2>
                    <p className="text-3xl tracking-tight text-gray-900">{product.price}</p>
            
            
            
                    <form className="mt-10">
                  
                    <div>
                      <p>{product.color}</p>
                    </div>
            
                    {/* Sizes */}
                    <div className="mt-10">
                        <div className="flex items-center justify-between">
                        <h3 className="text-sm font-medium text-gray-900">Size</h3>
                        <a href="#" className="text-sm font-medium text-indigo-600 hover:text-indigo-500">
                            Size guide
                        </a>
                        </div>
            
                        <fieldset aria-label="Choose a size" className="mt-4">
                        <RadioGroup
                            value={selectedSize}
                            onChange={setSelectedSize}
                            className="grid grid-cols-4 gap-4 sm:grid-cols-8 lg:grid-cols-4"
                        >
                            {product.sizes.map((size) => (
                            <Radio
                                key={size.name}
                                value={size}
                                disabled={!size.inStock}
                                className={classNames(
                                size.inStock
                                    ? 'cursor-pointer bg-white text-gray-900 shadow-sm'
                                    : 'cursor-not-allowed bg-gray-50 text-gray-200',
                                'group relative flex items-center justify-center rounded-md border px-4 py-3 text-sm font-medium uppercase hover:bg-gray-50 focus:outline-none data-[focus]:ring-2 data-[focus]:ring-indigo-500 sm:flex-1 sm:py-6',
                                )}
                            >
                                <span>{size.name}</span>
                                {size.inStock ? (
                                <span
                                    aria-hidden="true"
                                    className="pointer-events-none absolute -inset-px rounded-md border-2 border-transparent group-data-[focus]:border group-data-[checked]:border-indigo-500"
                                />
                                ) : (
                                <span
                                    aria-hidden="true"
                                    className="pointer-events-none absolute -inset-px rounded-md border-2 border-gray-200"
                                >
                                    <svg
                                    stroke="currentColor"
                                    viewBox="0 0 100 100"
                                    preserveAspectRatio="none"
                                    className="absolute inset-0 h-full w-full stroke-2 text-gray-200"
                                    >
                                    <line x1={0} x2={100} y1={100} y2={0} vectorEffect="non-scaling-stroke" />
                                    </svg>
                                </span>
                                )}
                            </Radio>
                            ))}
                        </RadioGroup>
                        </fieldset>
                    </div>
            
                    <button
                        onClick={Handleaddtocart}
                        type="submit"
                        className="mt-10 flex w-full items-center justify-center rounded-md border border-transparent bg-indigo-600 px-8 py-3 text-base font-medium text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
                    >
                        Add to bag
                    </button>
                    </form>
                </div>
            
                <div className="py-10 lg:col-span-2 lg:col-start-1 lg:border-r lg:border-gray-200 lg:pb-16 lg:pr-8 lg:pt-6">
                    {/* Description and details */}
                    <div>
                    <h3 className="sr-only">Description</h3>
            
                    <div className="space-y-6">
                        <p className="text-base text-gray-900">{product.description}</p>
                    </div>
                    </div>
            
                    <div className="mt-10">
                    <h3 className="text-sm font-medium text-gray-900">Highlights</h3>
            
                    <div className="mt-4">
                        <ul role="list" className="list-disc space-y-2 pl-4 text-sm">
                        {product.highlights.map((highlight) => (
                            <li key={highlight} className="text-gray-400">
                            <span className="text-gray-600">{highlight}</span>
                            </li>
                        ))}
                        </ul>
                    </div>
                    </div>
            
                    <div className="mt-10">
                    <h2 className="text-sm font-medium text-gray-900">Details</h2>
            
                    <div className="mt-4 space-y-6">
                        <p className="text-sm text-gray-600">{product.details}</p>
                    </div>
                    </div>
                </div>
                </div>
                
               
                    </section>

      </div>
    </div>
  )
}
