import React, { useState } from 'react';

const Charityform = () => {
  const [donorType, setDonorType] = useState('parent');
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    address: '',
    donationType: '',
    studentDetails: '',
    institutionName: '',
    institutionType: '',
    donorType: 'parent', // Default to 'parent'
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3002/api/charity/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const result = await response.json();
        alert('Charity form submitted successfully!');
        console.log(result);
        setFormData({
          fullName: '',
          email: '',
          phone: '',
          address: '',
          donationType: '',
          studentDetails: '',
          institutionName: '',
          institutionType: '',
          donorType: 'parent',
        });
        setDonorType('parent');
      } else {
        const error = await response.json();
        alert(`Error: ${error.message || 'Failed to submit form.'}`);
      }
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('An unexpected error occurred.');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-10 px-5 lg:px-20 bg-cover bg-center">
      <div className="max-w-3xl mx-auto bg-white shadow-lg rounded-lg p-8">
        <h1 className="text-2xl font-semibold text-gray-800 mb-8 text-center">Charity Contribution Form</h1>

        {/* Donor Type Selection */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">I am a:</label>
          <div className="flex space-x-4">
            <button
              type="button"
              onClick={() => {
                setDonorType('parent');
                setFormData({ ...formData, donorType: 'parent' });
              }}
              className={`py-2 px-4 rounded ${donorType === 'parent' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-600'}`}
            >
              Parent
            </button>
            <button
              type="button"
              onClick={() => {
                setDonorType('institution');
                setFormData({ ...formData, donorType: 'institution' });
              }}
              className={`py-2 px-4 rounded ${donorType === 'institution' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-600'}`}
            >
              Institution
            </button>
          </div>
        </div>

        {/* Form Fields */}
        <form className="space-y-6" onSubmit={handleSubmit}>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Full Name</label>
            <input
              type="text"
              name="fullName"
              value={formData.fullName}
              onChange={handleInputChange}
              className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-indigo-500"
              placeholder="Enter your full name"
              required
            />
          </div>
          {/* ... (Rest of the input fields remain the same as above) */}
        </form>
      </div>
    </div>
  );
};

export default Charityform;
