// ProductDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { boysuniformsData } from '../../../Data/Boys/Boysuniform'; // Import boys data
import { girlsuniformData } from '../../../Data/Girls/Girlsuniform'; // Import girls data

const ProductDetails = () => {
  const { productId } = useParams(); // Get productId from URL params
  const [product, setProduct] = useState(null);

  // Combine boys and girls data
  const combinedData = [...boysuniformsData, ...girlsuniformData];

  // Find the product by its ID
  useEffect(() => {
    const foundProduct = combinedData
      .flatMap((institution) =>
        institution.uniforms.find((uniform) => uniform.id === productId)
      )
      .find((uniform) => uniform.id === productId);

    setProduct(foundProduct); // Set the found product to state
  }, [productId]);

  if (!product) {
    return <div>Loading...</div>; // Show loading state while fetching the product
  }

  return (
    <div className="py-12 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="lg:grid lg:grid-cols-2 lg:gap-8">
          {/* Product Image */}
          <div className="lg:max-w-lg lg:w-full lg:h-full">
            <img
              src={product.imageUrl}
              alt={product.name}
              className="w-full h-full object-center object-cover"
            />
          </div>

          {/* Product Information */}
          <div className="mt-4 lg:mt-0 lg:col-span-1">
            <h1 className="text-3xl font-extrabold text-gray-900">{product.name}</h1>
            <p className="text-xl text-gray-700 mt-4">{product.description}</p>

            {/* Price */}
            <p className="text-2xl font-semibold text-gray-900 mt-6">{product.price} â‚¹</p>

            {/* Add to Cart Button */}
            <button className="mt-6 px-4 py-2 bg-indigo-600 text-white font-semibold rounded-md">
              Add to Cart
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
