import React from "react";

const ProductCard = ({ product }) => {
  return (
    <div className="max-w-sm rounded overflow-hidden shadow-lg bg-white hover:shadow-2xl transition-shadow duration-300">
      <img
        className="w-full h-64 object-cover"
        src={product.image}
        alt={product.type}
      />
      <div className="p-4">
        <h2 className="font-bold text-xl text-gray-800">{product.type}</h2>
        <p className="text-gray-500 text-sm">{product.description}</p>
        <div className="flex justify-between items-center mt-4">
          <span className="text-xl font-semibold text-gray-900">${product.price}</span>
          <button className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 focus:outline-none">
            Add to Cart
          </button>
        </div>
        <div className="mt-2">
          <p className="text-sm text-gray-700">Available Sizes:</p>
          <ul className="flex space-x-2">
            {product.sizes.map((size) => (
              <li
                key={size}
                className="px-3 py-1 bg-gray-100 text-gray-700 rounded-full text-xs"
              >
                {size}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
