import { useTheme } from '@emotion/react';
import { useMediaQuery } from '@mui/material';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const menu = [
  { name: "Dashboard", path: "/admin" },
  { name: "Products", path: "/admin/products" },
  { name: "Customers", path: "/admin/customers" },
  { name: "Orders", path: "/admin/orders" },
  { name: "Add Product", path: "/admin/product/CreateProduct" },
];

const AdminMainPanel = () => {
  const theme = useTheme();
  const isLargerScreen = useMediaQuery(theme.breakpoints.up("lg"));
  const [sidebarVisible, setSidebarVisible] = useState(true);
  const navigate = useNavigate();

  // Function to toggle sidebar visibility on smaller screens
  const toggleSidebar = () => {
    setSidebarVisible(!sidebarVisible);
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div
        className={`${
          sidebarVisible ? 'block' : 'hidden'
        } lg:block w-64 bg-gray-800 text-white p-4 space-y-6`}
      >
        <h2 className="text-xl font-semibold">Admin Panel</h2>
        <ul className="space-y-4">
          {menu.map((item) => (
            <li key={item.name}>
              <button
                onClick={() => navigate(item.path)}
                className="w-full text-left px-4 py-2 hover:bg-gray-700 rounded-md"
              >
                {item.name}
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* Main content area */}
      <div className="flex-1 bg-gray-100 p-6">
        {/* Toggle button for small screens */}
        <button
          onClick={toggleSidebar}
          className="lg:hidden p-4 bg-gray-800 text-white rounded-md"
        >
          Toggle Sidebar
        </button>

        {/* Dashboard content */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mt-6">
          {/* Card for Dashboard stats */}
          <div className="bg-white p-6 rounded-lg shadow-lg flex flex-col justify-between">
            <h3 className="text-lg font-semibold">Total Products</h3>
            <p className="text-3xl font-bold text-blue-600">120</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-lg flex flex-col justify-between">
            <h3 className="text-lg font-semibold">Total Orders</h3>
            <p className="text-3xl font-bold text-green-600">50</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-lg flex flex-col justify-between">
            <h3 className="text-lg font-semibold">Total Customers</h3>
            <p className="text-3xl font-bold text-purple-600">30</p>
          </div>
        </div>

        {/* More content can go here */}
      </div>
    </div>
  );
};

export default AdminMainPanel;
