import React, { useState } from 'react';
import { boysuniformsData } from '../../../Data/Boys/Boysuniform'; // Import boys' data
import { girlsuniformData } from '../../../Data/Girls/Girlsuniform'; // Import girls' data
import ProductCard from './ProductCard'; // Import the ProductCard component

const sortOptions = [
  { name: 'Price: Low to High', value: 'low-to-high' },
  { name: 'Price: High to Low', value: 'high-to-low' },
];

const institutionTypes = [
  { name: 'All', value: 'all' },
  { name: 'School', value: 'school' },
  { name: 'College', value: 'college' },
];

const genderTypes = [
  { name: 'Boys', value: 'boys' },
  { name: 'Girls', value: 'girls' },
  { name: 'All', value: 'all' },
];

const productsPerPage = 12; // Number of products to display per page

export default function ProductPage() {
  const [selectedGender, setSelectedGender] = useState('all');
  const [selectedInstitutionType, setSelectedInstitutionType] = useState('all');
  const [selectedSortOption, setSelectedSortOption] = useState('low-to-high');
  const [currentPage, setCurrentPage] = useState(1);

  // Combine boys and girls data based on the selected gender filter
  const filteredData = 
    selectedGender === 'boys'
      ? boysuniformsData
      : selectedGender === 'girls'
      ? girlsuniformData
      : [...boysuniformsData, ...girlsuniformData];

  // Filter by institution type
  const filteredByInstitution = filteredData.filter((uniform) =>
    selectedInstitutionType === 'all'
      ? true
      : uniform.institutionType.toLowerCase() === selectedInstitutionType
  );

  // Sort the data based on selected price order
  const sortedData = filteredByInstitution.sort((a, b) =>
    selectedSortOption === 'low-to-high' ? a.price - b.price : b.price - a.price
  );

  // Paginate the data
  const startIndex = (currentPage - 1) * productsPerPage;
  const paginatedData = sortedData.slice(startIndex, startIndex + productsPerPage);

  const totalPages = Math.ceil(sortedData.length / productsPerPage);

  const handlePageChange = (page) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page);
    }
  };

  return (
    <div className="bg-gray-50">
      {/* Filters Section */}
      <div className="container mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center">
          <h1 className="text-3xl font-bold text-gray-900">Uniforms</h1>
          <div className="flex space-x-4">
            <button
              onClick={() => setSelectedGender('all')}
              className={`px-4 py-2 rounded-md ${selectedGender === 'all' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-600'}`}
            >
              All
            </button>
            <button
              onClick={() => setSelectedGender('boys')}
              className={`px-4 py-2 rounded-md ${selectedGender === 'boys' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-600'}`}
            >
              Boys
            </button>
            <button
              onClick={() => setSelectedGender('girls')}
              className={`px-4 py-2 rounded-md ${selectedGender === 'girls' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-600'}`}
            >
              Girls
            </button>
          </div>
        </div>

        {/* Filters for Institution and Sort */}
        <div className="mt-6 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          <div className="space-y-2">
            <label className="text-sm font-medium text-gray-700">Institution</label>
            <select
              value={selectedInstitutionType}
              onChange={(e) => setSelectedInstitutionType(e.target.value)}
              className="w-full p-2 border rounded-md"
            >
              {institutionTypes.map((type) => (
                <option key={type.value} value={type.value}>
                  {type.name}
                </option>
              ))}
            </select>
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium text-gray-700">Sort By</label>
            <select
              value={selectedSortOption}
              onChange={(e) => setSelectedSortOption(e.target.value)}
              className="w-full p-2 border rounded-md"
            >
              {sortOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.name}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Product Grid Section */}
      <div className="container mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {paginatedData.map((uniform) => (
            <ProductCard key={uniform.id} uniform={uniform} institution={uniform.institution} />
          ))}
        </div>

        {/* Pagination Section */}
        <div className="flex justify-center mt-8">
          <button
            onClick={() => handlePageChange(currentPage - 1)}
            disabled={currentPage === 1}
            className={`px-4 py-2 rounded-l-md ${currentPage === 1 ? 'bg-gray-300 text-gray-500' : 'bg-indigo-600 text-white'}`}
          >
            Previous
          </button>

          {[...Array(totalPages)].map((_, index) => {
            const page = index + 1;
            return (
              <button
                key={page}
                onClick={() => handlePageChange(page)}
                className={`px-4 py-2 ${currentPage === page ? 'bg-indigo-600 text-white' : 'bg-white text-indigo-600 border border-indigo-600'} rounded-md`}
              >
                {page}
              </button>
            );
          })}

          <button
            onClick={() => handlePageChange(currentPage + 1)}
            disabled={currentPage === totalPages}
            className={`px-4 py-2 rounded-r-md ${currentPage === totalPages ? 'bg-gray-300 text-gray-500' : 'bg-indigo-600 text-white'}`}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
}
