import React, { useState } from 'react';
import './ProductCard.css';
import { useNavigate } from 'react-router-dom';
import { girlsuniformData } from '../../../Data/Girls/Girlsuniform';
import { boysuniformsData } from '../../../Data/Boys/Boysuniform';

// Correctly map the institutions for boys and girls
const boysdata = boysuniformsData.map((item) => item.institution); // Mapping to 'institution'
const girlsdata = girlsuniformData.map((item) => item.institution); // Mapping to 'institution'

const ProductCard = ({ uniform }) => {
  const navigate = useNavigate();

  // Safeguard: Check if 'uniform.institution' is defined before accessing 'institutionType'
  const institution = uniform.institution || {}; // Ensure institution is not undefined
  const institutionType = institution.institutionType || "Unknown Institution Type"; // Default to unknown if undefined
  const institutionName = institution.name || "Unknown Institution"; // Default to unknown if undefined

  return (
    <div onClick={() => navigate(`/product/${uniform.id}`)} className="institutionCard w-[20rem] m-3 transition-all cursor-pointer">
      <div className="productCard">
        <div>
          {/* Uniform Image */}
          <img
            src={uniform.image}
            alt={`${uniform.type} Image`}
            className="w-[18rem] h-[18rem] object-cover object-left-top"
          />
        </div>
        <div className="textpart bg-white p-3">
          {/* Uniform Description */}
          <p>{uniform.description}</p>

          {/* Institution Type */}
          <p className="text-gray-600 mb-2">Type: {institutionType}</p>

          {/* Uniform Color */}
          <p className="text-blue-800 font-bold">Color: {uniform.color}</p>

          {/* Institution Name */}
          <p className="font-bold opacity-60 text-wrap">Institute: {institutionName}</p>

          {/* Price */}
          <p className="font-semibold text-green-600">Rs. {uniform.price}</p>

          {/* Quality */}
          <p>Quality: {uniform.quality}</p>

          {/* Available Sizes */}
          <p>Sizes: {uniform.sizes}</p> {/* Properly rendering sizes */}
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
