// ProductCard.js
import React from 'react';
import { Link } from 'react-router-dom'; // Import Link from React Router for navigation

const ProductCard = ({ uniform }) => {
  return (
    <div className="relative flex flex-col justify-between">
      <Link
        to={`/product/${uniform.id}`} // Navigate to product details page using product id
        className="group"
      >
        <div className="h-72 w-full bg-gray-200 rounded-md overflow-hidden">
          <img
            src={uniform.imageUrl}
            alt={uniform.name}
            className="w-full h-full object-center object-cover group-hover:opacity-75"
          />
        </div>
        <div className="mt-4 text-center">
          <h3 className="text-sm text-gray-700">{uniform.name}</h3>
          <p className="mt-1 text-lg font-medium text-gray-900">{uniform.price} â‚¹</p>
        </div>
      </Link>
    </div>
  );
};

export default ProductCard;
