import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Step, StepLabel, Stepper, Button } from '@mui/material';

const steps = ["Placed", "Order Confirmed", "Order Shipped", "Order Delivered"];

const AdminOrderTracker = () => {
  const [orders, setOrders] = useState([]);
  
  useEffect(() => {
    // Fetch all orders from the backend
    const fetchOrders = async () => {
      try {
        const response = await axios.get('/api/admin/orders');
        setOrders(response.data.orders);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchOrders();
  }, []);

  const updateOrderStatus = async (orderId, newStatus) => {
    try {
      const response = await axios.put(`/api/admin/orders/${orderId}`, { status: newStatus });
      alert('Order status updated!');
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order._id === orderId ? { ...order, status: newStatus } : order
        )
      );
    } catch (error) {
      console.error('Error updating order status:', error);
    }
  };

  return (
    <div className="admin-order-tracker">
      <h2 className="text-2xl mb-4">Admin Order Tracker</h2>
      {orders.map((order) => {
        const activeStep = steps.indexOf(order.status);
        
        return (
          <div key={order._id} className="order-tracker mb-4 p-4 border">
            <h3 className="font-bold">Order ID: {order._id}</h3>
            <p>Customer: {order.customerName}</p>
            <p>Status: {order.status}</p>

            {/* Order Status Tracker */}
            <Stepper activeStep={activeStep} alternativeLabel>
              {steps.map((label, index) => (
                <Step key={index}>
                  <StepLabel sx={{ fontSize: '16px', color: activeStep >= index ? '#9155FD' : 'gray' }}>
                    {label}
                  </StepLabel>
                </Step>
              ))}
            </Stepper>

            {/* Button to change the order status */}
            <div className="mt-4">
              {activeStep < steps.length - 1 && (
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => updateOrderStatus(order._id, steps[activeStep + 1])}
                >
                  Mark as {steps[activeStep + 1]}
                </Button>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default AdminOrderTracker;
