import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const AdminPanel = () => {
  const [adminEmail, setAdminEmail] = useState('');
  const [adminPassword, setAdminPassword] = useState('');

  const handleLogin = () => {
    if (adminEmail === 'admin@example.com' && adminPassword === 'admin123') {
      // Simulate successful login (use JWT in a real scenario)
      localStorage.setItem('adminToken', 'your-jwt-token-here');
      alert('Logged in as admin');
    } else {
      alert('Invalid credentials');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full sm:w-96">
        <h1 className="text-2xl font-semibold text-center mb-6">Admin Panel</h1>
        
        <div className="space-y-4">
          <input
            type="email"
            placeholder="Admin Email"
            className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400"
            value={adminEmail}
            onChange={(e) => setAdminEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400"
            value={adminPassword}
            onChange={(e) => setAdminPassword(e.target.value)}
          />
          <button
            onClick={handleLogin}
            className="w-full bg-blue-500 text-white py-3 rounded-md hover:bg-blue-600"
          >
            Login
          </button>
        </div>

        <div className="mt-6">
          <h2 className="text-xl font-medium text-center">Admin Dashboard</h2>
          <ul className="space-y-3 mt-4">
            <li>
              <Link
                to="/admin/products"
                className="block text-blue-600 hover:text-blue-800 text-center"
              >
                Manage Products
              </Link>
            </li>
            <li>
              <Link
                to="/admin/orders"
                className="block text-blue-600 hover:text-blue-800 text-center"
              >
                Order Tracker
              </Link>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
