import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminOrderTracker = () => {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [trackingNumber, setTrackingNumber] = useState('');
  const [courierService, setCourierService] = useState('');
  const [estimatedDeliveryDate, setEstimatedDeliveryDate] = useState('');

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const token = localStorage.getItem('adminToken');
        const response = await axios.get('/orders', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setOrders(response.data);
      } catch (error) {
        alert('Error fetching orders');
      }
    };

    fetchOrders();
  }, []);

  const handleSelectOrder = (order) => {
    setSelectedOrder(order);
    setTrackingNumber(order.shipmentTracking?.trackingNumber || '');
    setCourierService(order.shipmentTracking?.courierService || '');
    setEstimatedDeliveryDate(order.shipmentTracking?.estimatedDeliveryDate || '');
  };

  const handleUpdateTracking = async () => {
    try {
      const token = localStorage.getItem('adminToken');
      await axios.put(
        `/orders/track/${selectedOrder._id}`,
        {
          trackingNumber,
          courierService,
          estimatedDeliveryDate,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Shipment tracking updated');
    } catch (error) {
      alert('Error updating shipment tracking');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h2 className="text-2xl font-semibold text-center mb-8">Order Tracker</h2>
      
      {/* Orders Table */}
      <div className="overflow-x-auto shadow-lg rounded-lg">
        <table className="min-w-full table-auto bg-white">
          <thead>
            <tr className="bg-blue-500 text-white">
              <th className="py-3 px-4 text-left">Order ID</th>
              <th className="py-3 px-4 text-left">User</th>
              <th className="py-3 px-4 text-left">Total Amount</th>
              <th className="py-3 px-4 text-left">Status</th>
              <th className="py-3 px-4 text-left">Action</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id} className="border-t">
                <td className="py-3 px-4">{order._id}</td>
                <td className="py-3 px-4">{order.user ? order.user.fullName : 'Unknown'}</td>
                <td className="py-3 px-4">${order.totalAmount}</td>
                <td className="py-3 px-4">{order.status}</td>
                <td className="py-3 px-4">
                  {order.status === 'Shipped' && (
                    <button
                      onClick={() => handleSelectOrder(order)}
                      className="text-blue-500 hover:text-blue-700"
                    >
                      Track
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Shipment Details */}
      {selectedOrder && (
        <div className="mt-8 bg-white shadow-lg rounded-lg p-6">
          <h3 className="text-xl font-medium mb-4">Shipment Details for Order: {selectedOrder._id}</h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block font-medium">Tracking Number</label>
              <input
                type="text"
                value={trackingNumber}
                onChange={(e) => setTrackingNumber(e.target.value)}
                className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
            </div>

            <div>
              <label className="block font-medium">Courier Service</label>
              <input
                type="text"
                value={courierService}
                onChange={(e) => setCourierService(e.target.value)}
                className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
            </div>

            <div>
              <label className="block font-medium">Estimated Delivery Date</label>
              <input
                type="date"
                value={estimatedDeliveryDate}
                onChange={(e) => setEstimatedDeliveryDate(e.target.value)}
                className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
            </div>
          </div>

          <button
            onClick={handleUpdateTracking}
            className="mt-6 w-full bg-blue-500 text-white py-3 rounded-md hover:bg-blue-600"
          >
            Update Tracking
          </button>
        </div>
      )}
    </div>
  );
};

export default AdminOrderTracker;
