import React from 'react';
import './ProductCard.css';
import { useNavigate } from 'react-router-dom';

const ProductCard = ({ uniform, institution }) => {
  const navigate = useNavigate();

  // Ensure the uniform ID is dynamically passed to the product detail page
  const handleClick = () => {
    navigate(`/product/${uniform.id}`);
  };

  return (
    <div onClick={handleClick} className="institutionCard w-[20rem] m-3 transition-all cursor-pointer">
      <div className="productCard">
        <div>
          {/* Uniform Image */}
          <img
            src={uniform.image}
            alt={`${uniform.type} Image`}
            className="w-[18rem] h-[18rem] object-cover object-left-top"
          />
        </div>
        <div className="textpart bg-white p-3">
          {/* Uniform Description */}
          <p>{uniform.description}</p>

          {/* Institution Type */}
          <p className="text-gray-600 mb-2">Type: {institution.institutionType}</p>

          {/* Uniform Color */}
          <p className="text-blue-800 font-bold">Color: {uniform.color}</p>

          {/* Institution Name */}
          <p className="font-bold opacity-60 text-wrap">Institute: {institution.name}</p>

          {/* Price */}
          <p className="font-semibold text-green-600">Rs. {uniform.price}</p>

          {/* Quality */}
          <p>Quality: {uniform.quality}</p>

          {/* Available Sizes */}
          <p>Sizes: {uniform.sizes.join(', ')}</p>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
